import{_ as e,o as t,c as o,a as s}from"./app.7e5cfeff.js";const _=JSON.parse('{"title":"Centos\u5206\u533A\u5408\u5E76","description":"","frontmatter":{},"headers":[],"relativePath":"devops/others/Centos\u5206\u533A\u5408\u5E76.md","lastUpdated":1670161814000}'),a={name:"devops/others/Centos\u5206\u533A\u5408\u5E76.md"},i=s('<h1 id="centos\u5206\u533A\u5408\u5E76" tabindex="-1">Centos\u5206\u533A\u5408\u5E76 <a class="header-anchor" href="#centos\u5206\u533A\u5408\u5E76" aria-hidden="true">#</a></h1><blockquote><p>\u672C\u6587\u6839\u636EUP\u4E3B<a href="https://space.bilibili.com/253977174" target="_blank" rel="noreferrer">\u5DE6\u5DE6\u7ECF\u9A8C</a>\u89C6\u9891\u6574\u7406\u800C\u6210</p></blockquote><p>\u5728<code>\u7269\u7406\u673A\u5B89\u88C5Linux\u540C\u65F6...</code> \u4E00\u8282\u4E3A\u4E86\u642D\u5EFA\u81EA\u5DF1\u7684\u670D\u52A1\u5668, \u6240\u4EE5\u6211\u5728\u81EA\u5DF1\u7684\u5C0F\u4E3B\u673A\u4E0A\u88C5\u4E86\u4E00\u4E2A\u65B0\u7684Centos\u7CFB\u7EDF. \u88C5\u5B8C\u7CFB\u7EDF\u540E\u53D1\u73B0\u9ED8\u8BA4\u7684\u786C\u76D8\u5206\u6210\u4E86\u4E24\u4E2A\u533A, \u4E00\u4E2A\u662Froot, \u4E00\u4E2A\u662Fhome. \u8FD9\u5C31\u5BFC\u81F4root\u7A7A\u95F4\u8FC7\u5C0F, \u9700\u8981\u5C06home\u533A\u5408\u5E76\u5230root\u533A.</p><p><img src="https://cdn.jsdelivr.net/gh/scattter/blogweb/images/image-20221204212357967.png" alt="image-20221204212357967"></p><p>\u53EF\u4EE5\u770B\u5230, \u4E0A\u9762root\u5206\u533A\u662F 50G, home\u5206\u533A\u662F61G(\u8FD9\u4E2A\u786C\u76D8\u662F128G\u7684\u5783\u573E\u56FA\u6001).</p><ol><li><p>\u5220\u9664home\u5206\u533A \u4F7F\u7528<code>nano /etc/fstab</code> \u547D\u4EE4 <img src="https://cdn.jsdelivr.net/gh/scattter/blogweb/images/image-20221204213104007.png" alt="image-20221204213104007"></p></li><li><p>\u5378\u8F7Dhome\u5206\u533A <code>umount /home</code> \u5378\u8F7D\u540E\u5982\u4E0B\u6240\u793A <img src="https://cdn.jsdelivr.net/gh/scattter/blogweb/images/image-20221204213314364.png" alt="image-20221204213314364"></p></li><li><p>\u67E5\u770B\u6709\u54EA\u4E9Blv\u5206\u533A <code>lvscan</code><img src="https://cdn.jsdelivr.net/gh/scattter/blogweb/images/image-20221204213453675.png" alt="image-20221204213453675"></p></li><li><p>\u5378\u8F7Dhome\u5206\u533A <code>lvremove /dev/mapper/centos-home</code><img src="https://cdn.jsdelivr.net/gh/scattter/blogweb/images/image-20221204213622124.png" alt="image-20221204213622124"></p></li><li><p>\u67E5\u770B\u5F53\u524D\u5269\u4F59\u7A7A\u95F4 <code>vgdisplay</code><img src="https://cdn.jsdelivr.net/gh/scattter/blogweb/images/image-20221204213816271.png" alt="image-20221204213816271"></p></li><li><p>\u5C06\u7A7A\u95F2\u7684\u7A7A\u95F4\u6269\u5BB9\u5230root <code>lvextend -l +100%free /dev/mapper/centos-root</code><img src="https://cdn.jsdelivr.net/gh/scattter/blogweb/images/image-20221204214013919.png" alt="image-20221204214013919"> \u6B64\u65F6\u7A7A\u95F4\u8FD8\u6CA1\u6709\u53D8\u5927 <img src="https://cdn.jsdelivr.net/gh/scattter/blogweb/images/image-20221204214049819.png" alt="image-20221204214049819"></p></li><li><p>\u518D\u6267\u884C\u547D\u4EE4\u5237\u65B0 <code>xfs_growfs /dev/mapper/centos-root</code> \u6B64\u65F6\u53EF\u4EE5\u770B\u5230\u5BB9\u91CF\u66F4\u65B0\u6210\u529F\u4E86 <img src="https://cdn.jsdelivr.net/gh/scattter/blogweb/images/image-20221204214307953.png" alt="image-20221204214307953"></p></li></ol>',6),c=[i];function r(g,n,l,p,d,m){return t(),o("div",null,c)}const b=e(a,[["render",r]]);export{_ as __pageData,b as default};
