import{_ as e,o as a,c as p,d as i}from"./app.ef78ef4f.js";const g=JSON.parse('{"title":"\u516C\u7F51\u8BBF\u95EEDDNS+IPV6","description":"","frontmatter":{},"headers":[{"level":2,"title":"1. \u5149\u732B\u6865\u63A5","slug":"_1-\u5149\u732B\u6865\u63A5","link":"#_1-\u5149\u732B\u6865\u63A5","children":[{"level":3,"title":"1.1 \u540E\u53F0\u4FE1\u606F","slug":"_1-1-\u540E\u53F0\u4FE1\u606F","link":"#_1-1-\u540E\u53F0\u4FE1\u606F","children":[]},{"level":3,"title":"1.2 \u5149\u732B\u8BBE\u7F6E","slug":"_1-2-\u5149\u732B\u8BBE\u7F6E","link":"#_1-2-\u5149\u732B\u8BBE\u7F6E","children":[]},{"level":3,"title":"1.3 \u8DEF\u7531\u5668\u62E8\u53F7","slug":"_1-3-\u8DEF\u7531\u5668\u62E8\u53F7","link":"#_1-3-\u8DEF\u7531\u5668\u62E8\u53F7","children":[]}]}],"relativePath":"devops/others/\u516C\u7F51\u8BBF\u95EE(DDNS+IPV6).md","lastUpdated":1692536012000}'),r={name:"devops/others/\u516C\u7F51\u8BBF\u95EE(DDNS+IPV6).md"},t=i('<h1 id="\u516C\u7F51\u8BBF\u95EEddns-ipv6" tabindex="-1">\u516C\u7F51\u8BBF\u95EEDDNS+IPV6 <a class="header-anchor" href="#\u516C\u7F51\u8BBF\u95EEddns-ipv6" aria-hidden="true">#</a></h1><blockquote><p>\u8FD1\u671F\u7531\u4E8E\u81EA\u5DF1\u642D\u5EFA\u4E86\u4E00\u4E2Anas, \u6240\u4EE5\u6709\u5916\u7F51\u8BBF\u95EE\u5BB6\u4E2D\u8DEF\u7531(\u670D\u52A1\u5668)\u7684\u9700\u6C42. \u6700\u5FEB\u6377\u7684\u65B9\u6CD5\u5C31\u662F\u6709\u4E00\u4E2A\u516C\u7F51ip, \u4F46\u662F\u73B0\u5728ipv4\u5730\u5740\u6709\u8FD9\u4E48\u7A00\u7F3A, \u6240\u4EE5\u8FD9\u4E00\u65B9\u6848\u5C31\u6682\u65F6pass. \u540C\u65F6, zt\u548Ctailscale\u7684\u901F\u5EA6\u4E5F\u4F9D\u8D56\u4E8E\u96A7\u9053\u4E24\u7AEF\u670D\u52A1\u5668, \u4E0D\u591F\u7A33\u5B9A, \u4E14\u901F\u5EA6\u4E5F\u8FDC\u8FDC\u8FBE\u4E0D\u5230\u76F4\u8FDE\u7684\u6548\u679C, \u6240\u4EE5\u5C31\u9700\u8981\u5BFB\u627E\u5176\u4ED6\u65B9\u6848.</p><p>ipv6\u662F\u65B0\u4E00\u4EE3\u7684ip\u5730\u5740\u534F\u8BAE, \u5176\u4E3A\u4E86\u89E3\u51B3ipv4\u5730\u5740\u4E0D\u591F\u4F7F\u7528\u7684\u95EE\u9898\u800C\u8BDE\u751F, \u6570\u91CF\u7EA7\u76F8\u6BD4\u4E8Eipv4\u662FN\u591A\u500D, \u6240\u4EE5\u51E0\u4E4E\u6BCF\u4E2A\u8DEF\u7531\u90FD\u6709\u4E00\u4E2Aipv6\u5730\u5740. \u56E0\u6B64, \u6211\u4EEC\u53EF\u4EE5\u501F\u7528ipv6\u8FDB\u884C\u516C\u7F51\u8BBF\u95EE.</p></blockquote><blockquote><p>\u7531\u4E8E\u4E00\u4E9B\u8BBE\u5907/\u57DF\u540D\u8FD8\u6CA1\u51C6\u5907\u597D, \u5468\u672B\u53EA\u505A\u4E86\u4E00\u90E8\u5206\u5148\u52A0\u4E0A, \u5176\u4ED6\u7684\u540E\u7EED\u6162\u6162\u66F4\u65B0.</p></blockquote><h2 id="_1-\u5149\u732B\u6865\u63A5" tabindex="-1">1. \u5149\u732B\u6865\u63A5 <a class="header-anchor" href="#_1-\u5149\u732B\u6865\u63A5" aria-hidden="true">#</a></h2><p>\u56E0\u4E3Aipv6\u5730\u5740\u5F88\u957F, \u540C\u65F6\u53C8\u4E0D\u597D\u8BB0\u5FC6, \u6240\u4EE5\u5728\u65E5\u5E38\u7F51\u9875\u8BBF\u95EE\u8FC7\u7A0B\u4E2D\u76F4\u63A5\u4F7F\u7528ipv6\u5730\u5740\u662F\u4E0D\u73B0\u5B9E\u7684, \u8FD9\u5C31\u9700\u8981\u7528\u5230\u57DF\u540D\u6765\u6620\u5C04ipv6\u5730\u5740, \u8FDB\u884C\u5B9E\u73B0\u5FEB\u6377\u8BBF\u95EE, \u8FD9\u5C31\u662FDNS( Domain Name System).</p><p>\u6B64\u5916, \u867D\u7136\u6BCF\u4E00\u53F0\u8DEF\u7531\u5668\u90FD\u6709\u4E00\u4E2A\u516C\u7F51ipv6\u5730\u5740, \u4F46\u662F\u8FD0\u8425\u5546\u6BCF\u9694\u4E00\u6BB5\u65F6\u95F4\u4F1A\u91CD\u7F6E\u6211\u4EEC\u7684ipv6\u5730\u5740, \u6240\u4EE5\u6211\u4EEC\u8981\u52A8\u6001\u7684\u66F4\u65B0\u6211\u4EEC\u7684\u57DF\u540D\u548Cipv6\u5730\u5740\u7684\u6620\u5C04\u5173\u7CFB, \u4E5F\u5C31\u662FDDNS(Dynamatic Domain Name System).</p><p>\u4E00\u822C\u6765\u8BB2, \u6211\u4EEC\u81EA\u5DF1\u914D\u7684\u8DEF\u7531\u5668\u6027\u80FD\u4F1A\u66F4\u597D\u4E00\u70B9, \u6240\u4EE5\u4E3A\u4E86\u66F4\u597D\u7684\u8FDB\u884CDDNS, \u53D1\u6325\u8DEF\u7531\u5668\u7684\u6548\u679C, \u6211\u4EEC\u9996\u5148\u9700\u8981\u9009\u62E9ddns\u9002\u914D\u4E0D\u9519\u7684\u8DEF\u7531\u5668, \u7136\u540E\u4F7F\u7528\u8DEF\u7531\u5668\u8FDB\u884C\u62E8\u53F7, \u5149\u732B\u6865\u63A5, \u501F\u52A9\u8DEF\u7531\u5668\u7684ddns\u5C06\u81EA\u5DF1\u7684\u57DF\u540D\u548Cipv6\u5730\u5740\u8FDB\u884C\u4E00\u4E2A\u52A8\u6001\u66F4\u65B0.</p><p>\u5728\u8FD9\u4E00\u6B65, \u6211\u4EEC\u5148\u8FDB\u884C\u5149\u732B\u6865\u63A5, \u8DEF\u7531\u5668\u62E8\u53F7\u7684\u64CD\u4F5C.</p><p>\u9996\u5148\u9700\u8981\u8FDB\u5165\u5149\u732B\u7684\u540E\u53F0, \u4E00\u822C\u4E3A<code>192.168.1.1</code>, \u540C\u65F6\u6211\u4EEC\u9700\u8981\u6839\u636E\u5149\u732B\u7684\u578B\u53F7\u53BB\u7F51\u4E0A\u67E5\u627E\u8D85\u7BA1\u5BC6\u7801, \u6CA1\u6709\u8D85\u7BA1\u5BC6\u7801\u6539\u4E0D\u4E86\u5149\u732B\u7684\u8FDE\u63A5\u65B9\u5F0F.</p><p>\u4EE5\u6211\u73B0\u5728\u79DF\u623F\u5B50\u79FB\u52A8\u7ED9\u7684<code>ZN-R173G</code> \u4E3A\u4F8B:</p><p>\u5149\u732B\u8D26\u53F7\u5BC6\u7801\u4E3A: <code>CMCCAdmin</code> \u548C <code>aDm8H%MdA</code></p><p>\u9664\u6B64\u4E4B\u5916, \u6211\u4EEC\u8FD8\u9700\u8981\u81EA\u5DF1\u5BBD\u5E26\u7684\u8D26\u53F7\u548C\u5BC6\u7801, \u8FD9\u4E24\u4E2A\u53EF\u4EE5\u6253\u8FD0\u8425\u5546\u7535\u8BDD\u83B7\u53D6.</p><h3 id="_1-1-\u540E\u53F0\u4FE1\u606F" tabindex="-1">1.1 \u540E\u53F0\u4FE1\u606F <a class="header-anchor" href="#_1-1-\u540E\u53F0\u4FE1\u606F" aria-hidden="true">#</a></h3><p>\u8FDB\u5165\u8DEF\u7531\u5668\u540E\u53F0\u540E, \u6211\u4EEC\u53EF\u4EE5\u770B\u5230\u5149\u732B\u7684\u4E00\u4E9B\u8FDE\u63A5\u4FE1\u606F, \u5176\u4E2D\u8868\u683C\u4E2D\u7B2C\u4E00\u884C <code>4_INTERNET_R_VID_4031</code> \u5C31\u662F\u6211\u4EEC\u5149\u732B\u548C\u4E92\u8054\u7F51\u8FDE\u63A5\u4F7F\u7528\u7684\u901A\u9053, \u8BB0\u4F4F\u6700\u540E\u7684VID: 4031, \u540E\u9762\u6211\u4EEC\u65B0\u589E\u6865\u63A5\u6A21\u5F0F\u7684\u65F6\u5019\u8981\u7528\u5230.</p><p>![image-20230820202156555](/Users/zhangke/Library/Application Support/typora-user-images/image-20230820202156555.png)</p><p>\u968F\u540E\u6211\u4EEC\u8FDB\u5165\u7F51\u7EDC\u680F, \u627E\u5230\u8FD9\u4E2A\u8FDE\u63A5, \u5C06\u6240\u6709\u7684\u914D\u7F6E\u90FD\u8BB0\u5F55\u4E0B\u6765.</p><p>![image-20230820202331325](/Users/zhangke/Library/Application Support/typora-user-images/image-20230820202331325.png)</p><h3 id="_1-2-\u5149\u732B\u8BBE\u7F6E" tabindex="-1">1.2 \u5149\u732B\u8BBE\u7F6E <a class="header-anchor" href="#_1-2-\u5149\u732B\u8BBE\u7F6E" aria-hidden="true">#</a></h3><p>\u8FDB\u5165\u7F51\u7EDC\u680F\u540E, \u627E\u5230\u4E0A\u9762\u8BF4\u7684\u8FDE\u63A5, \u8FDB\u884C\u5220\u9664, \u7136\u540E\u521B\u5EFA\u4E00\u4E2A\u65B0\u7684\u8FDE\u63A5.</p><p>\u5728\u51FA\u73B0\u7684\u914D\u7F6E\u4E2D, \u9009\u62E9\u6A21\u5F0F\u4E3A\u6865\u63A5, \u7C7B\u4F3C\u4E0B\u9762\u7684\u56FE, \u4E0D\u8FC7\u8FD9\u91CC\u9700\u8981\u628A<code>Vlan ID</code> \u6362\u4E3A\u4E0A\u4E00\u5C0F\u8282\u8BB0\u5F55\u7684<code>VID</code>, \u5982\u679C\u662F4031, \u90A3\u4E48\u5C31\u586B4031. \u5176\u4ED6\u548C\u4E4B\u524D\u4FDD\u6301\u4E00\u81F4\u5C31\u884C, \u5982\u4E0B</p><p>![image-20230820202551416](/Users/zhangke/Library/Application Support/typora-user-images/image-20230820202551416.png)</p><h3 id="_1-3-\u8DEF\u7531\u5668\u62E8\u53F7" tabindex="-1">1.3 \u8DEF\u7531\u5668\u62E8\u53F7 <a class="header-anchor" href="#_1-3-\u8DEF\u7531\u5668\u62E8\u53F7" aria-hidden="true">#</a></h3><p>\u8FDB\u5165\u8DEF\u7531\u5668\u7684\u914D\u7F6E\u9875\u9762, \u6211\u8FD9\u91CC\u6682\u65F6\u4F7F\u7528\u6590\u8BAFk2\u4E3A\u4F8B\u5B50, \u540E\u7EED\u6709\u65B0\u7684\u8DEF\u7531\u5668\u518D\u8FDB\u884C\u8865\u5145.</p><p>\u6590\u8BAF\u76F4\u63A5\u5230\u4E0A\u7F51\u9875\u9762\u8BBE\u7F6E\u5C31\u884C\u4E86, \u8FD9\u91CC\u9700\u8981\u6CE8\u610F\u4E00\u70B9, \u6709\u4E9B\u5730\u65B9\u53EF\u80FD\u9700\u8981\u5C06<strong>\u5149\u732B\u7684mac\u5730\u5740\u590D\u5236\u4E0B\u6765</strong>, \u5728\u4E0B\u9762\u7684\u9AD8\u7EA7\u8BBE\u7F6E\u91CC\u9762\u8BBE\u7F6E\u4E0B, \u5426\u5219\u79FB\u52A8\u90A3\u8FB9\u4E0D\u54CD\u5E94. \u4FDD\u5B58\u540E\u8DEF\u7531\u5668\u81EA\u52A8\u91CD\u542F, \u6B64\u65F6\u5C31\u662F\u8DEF\u7531\u5668\u62E8\u53F7.</p><p>![image-20230820204144491](/Users/zhangke/Library/Application Support/typora-user-images/image-20230820204144491.png)</p>',25),d=[t];function o(s,n,c,_,l,h){return a(),p("div",null,d)}const u=e(r,[["render",o]]);export{g as __pageData,u as default};
