import{_ as e,o as p,c as a,n as t}from"./app.3123c535.js";const g=JSON.parse('{"title":"\u6D45\u8C08\u4EBA\u5DE5\u667A\u80FD - \u8BFB\u540E\u5C0F\u7ED3","description":"","frontmatter":{},"headers":[],"relativePath":"pages/explore/read/\u6D45\u8C08\u4EBA\u5DE5\u667A\u80FD - \u8BFB\u540E\u6DF7\u4E71\u5C0F\u7ED3.md","lastUpdated":1725278912000}'),r={name:"pages/explore/read/\u6D45\u8C08\u4EBA\u5DE5\u667A\u80FD - \u8BFB\u540E\u6DF7\u4E71\u5C0F\u7ED3.md"},l=t('<h1 id="\u6D45\u8C08\u4EBA\u5DE5\u667A\u80FD-\u8BFB\u540E\u5C0F\u7ED3" tabindex="-1">\u6D45\u8C08\u4EBA\u5DE5\u667A\u80FD - \u8BFB\u540E\u5C0F\u7ED3 <a class="header-anchor" href="#\u6D45\u8C08\u4EBA\u5DE5\u667A\u80FD-\u8BFB\u540E\u5C0F\u7ED3" aria-hidden="true">#</a></h1><p>\u8FD9\u7BC7\u6587\u7AE0\u662F\u6211\u5728\u962E\u4E00\u5CF0\u8001\u5E08\u7684\u535A\u5BA2\u4E0A\u770B\u5230\u7684, \u6B63\u5E38\u6765\u8BF4, \u8FD9\u79CD\u63A8\u8350\u7684\u6587\u7AE0\u6211\u5927\u6982\u626B\u4E24\u773C\u5C31\u4F1A\u5173\u6389, \u4E0D\u8FC7\u626B\u4E86\u4E0B\u540E\u53D1\u73B0\u597D\u50CF\u5199\u7684\u8FD8\u4E0D\u9519, \u56E0\u6B64\u5C31\u8D81\u7740\u5468\u672B\u628A\u5B83\u770B\u5B8C\u4E86. \u5728\u770B\u7684\u8FC7\u7A0B\u4E2D\u4E5F\u6709\u4E00\u4E9B\u6742\u4E03\u6742\u516B\u7684\u60F3\u6CD5, \u4E3A\u4E86\u9632\u6B62\u8BFB\u5B8C\u540E\u4E00\u70B9\u4F5C\u7528\u90FD\u6CA1, \u7279\u5C06\u8FD9\u4E9B\u60F3\u6CD5\u8BB0\u5F55\u4E8E\u6B64.</p><p>\u8FD9\u7BC7\u957F\u6587\u4E2D\u8BA9\u6211\u5370\u8C61\u6BD4\u8F83\u6DF1\u523B\u7684\u5C31\u662F \u201C\u4E4C\u9E26\u667A\u80FD\u201D \u7684\u4F8B\u5B50, \u5373\u771F\u6B63\u7684\u667A\u80FD\u4F53\u5E94\u8BE5\u50CF\u4E4C\u9E26\u4E00\u6837, \u63A5\u53D7\u5916\u754C\u5C0F\u8F93\u5165, \u81EA\u6211\u5206\u6790\u5224\u65AD, \u5F97\u51FA\u6CDB\u6027\u7ED3\u8BBA, \u4EFB\u52A1\u5B9E\u65BD\u4E0E\u6539\u8FDB, \u5B8C\u6210\u4E00\u6574\u5957\u52A8\u4F5C. \u73B0\u5982\u4ECA\u6D41\u884C\u7684\u667A\u80FD\u4F53\u5462? \u53EA\u6709\u6A21\u578B\u7684\u642D\u5EFA\u548C\u8C03\u7528, \u53EF\u80FD\u4F1A\u6709\u6A21\u578B\u7684\u7EE7\u7EED\u8C03\u4F18, \u4F46\u4E5F\u65E0\u5F02\u4E8E\u996E\u9E20\u6B62\u6E34.</p><p>\u7528\u6211\u4EEC\u65E5\u5E38\u7F16\u7A0B\u7684\u89D2\u5EA6\u6765\u770B, \u73B0\u6709\u7684\u5927\u6A21\u578B\u7C7B\u4F3C\u4E8E\u65E0\u6570\u7684<code>if else</code> \u4EA6\u6216\u8005\u662F <code>switch</code> \u7B49\u5728\u56FA\u6709\u73AF\u5883\u4E0B\u7684\u51FD\u6570\u8C03\u7528, \u6CA1\u6709\u65B0\u77E5\u8BC6\u65B0\u80FD\u529B\u7684\u4EA7\u751F; \u5373\u4FBF\u6709\u4E00\u4E9B\u65B0\u7684\u77E5\u8BC6, \u5927\u90E8\u5206\u4E5F\u90FD\u662F\u6BEB\u65E0\u4F9D\u636E\u7684\u778E\u63A8\u7406. \u5C31\u50CF\u662F\u4E4B\u524D\u9762\u8BD5\u7684\u65F6\u5019\u95EE\u6211\u5BF9\u673A\u5668\u5B66\u4E60\u7684\u7406\u89E3, \u6211\u56DE\u7B54\u8BF4\u673A\u5668\u5B66\u4E60\u5C31\u50CF\u662F\u7B97\u547D, \u4F60\u628A\u4F60\u7684\u751F\u8FB0\u516B\u5B57\u7ED9\u4ED6(\u53EF\u4EE5\u7406\u89E3\u4E3A\u5927\u6A21\u578B),\u4ED6\u6839\u636E\u8FD9\u4E9B\u56FA\u5B9A\u4FE1\u606F\u4EE5\u53CA\u4F60\u5F53\u65F6\u7684\u52A8\u6001\u4FE1\u606F(\u795E\u60C5\u7B49) \u7ED9\u51FA\u4E00\u5957\u7B54\u6848. \u4ECE\u4E00\u4E2A\u516C\u5F0F\u5230\u53E6\u4E00\u4E2A\u7684\u516C\u5F0F, \u53EA\u4E0D\u8FC7\u8FD9\u4E2A\u516C\u5F0F\u66F4\u5927, \u66F4\u590D\u6742, \u4EE5\u81F3\u4E8E\u8BA9\u5927\u90E8\u5206\u4EBA\u4EA7\u751F\u4E86\u795E\u79D8\u548C\u656C\u754F\u611F.</p><p>\u5728\u6587\u4E2D\u4F5C\u8005\u4ECE\u4E4C\u9E26\u7684\u4F8B\u5B50\u5165\u624B, \u4E4C\u9E26\u6240\u80FD\u770B\u5230\u7684\u5C31\u662F\u98DF\u7269, \u94C1\u76D2\u5B50, \u7EA2\u7EFF\u706F, \u8FD9\u4E9B\u7269\u4F53\u5BA2\u89C2\u7684\u53BB\u770B\u90FD\u6BD4\u8F83\u72EC\u7ACB, \u4FE1\u606F\u91CF\u4E0D\u591A, \u4F46\u662F\u5982\u679C\u4ECE\u4E3B\u89C2\u4E0A\u53BB\u601D\u8003, \u5C31\u4F1A\u53D1\u73B0\u6BCF\u4E00\u4E2A\u7269\u4F53\u7684\u80CC\u540E\u90FD\u6709\u8BB8\u591A\u7684\u77E5\u8BC6. \u6BD4\u5982\u7EA2\u7EFF\u706F\u4F1A\u5F71\u54CD\u5230\u8F66\u8F86\u7684\u884C\u9A76, \u8F66\u8F86\u7684\u884C\u9A76\u4F1A\u5BF9\u8DEF\u4E0A\u7684\u98DF\u7269\u7B49\u4E1C\u897F\u9020\u6210\u5F71\u54CD. \u548C\u7269\u7406\u5B66\u4E2D\u7684 \u201C\u6697\u7269\u8D28\u201D \u7C7B\u4F3C, \u8FD9\u4E9B\u4FE1\u606F\u53EF\u4EE5\u7EDF\u79F0\u4E3A \u201C\u6697\u4FE1\u606F\u201D, \u5373\u4EBA\u4E4B\u6240\u4EE5\u806A\u660E, \u5C31\u662F\u56E0\u4E3A\u80FD\u591F\u83B7\u53D6\u5230\u8FD9\u4E9B \u201C\u6697\u4FE1\u606F\u201D.</p><p>\u9488\u5BF9\u8FD9\u4E9B \u201C\u6697\u4FE1\u606F\u201D \u7684\u7814\u7A76\u5C31\u662F \u201C\u5C0F \u6570\u636E\u3001\u5927\u4EFB\u52A1\u8303\u5F0F\u201D \u7684\u6A2A\u5411\u7814\u7A76, \u76F8\u6BD4\u4E8E\u73B0\u5728\u4E3B\u6D41\u7684 \u201C\u5927\u6570\u636E\u3001\u5C0F\u4EFB\u52A1\u8303\u5F0F\u201D \u7684\u5782\u76F4\u7814\u7A76, \u6A2A\u5411\u7814\u7A76\u7684\u5BF9\u8C61\u50CF\u662F\u5BF9\u5F00\u53D1\u51FA \u201Ckimi\u201D \u4EA7\u54C1\u7684\u6BCD\u516C\u53F8\u540D\u5B57 \u201C\u6708\u4E4B\u6697\u9762\u201D \u7684\u7814\u7A76. \u7528\u7814\u7A76\u65B9\u6CD5\u6765\u8BB2\u5C31\u662F\u4ECE\u5F52\u7EB3\u603B\u7ED3\u5230\u6F14\u7ECE\u63A8\u7406\u7684\u8F6C\u53D8.</p><p>\u5177\u4F53\u5230\u5728\u6D4F\u89C8\u8FC7\u7A0B\u4E2D\u4EA7\u751F\u7684\u4E00\u4E9B\u60F3\u6CD5\u5982\u4E0B:</p><ul><li>\u65F6\u95F4\u7EF4\u5EA6\u662F\u5426\u6709\u7528?</li></ul><p>\u4E66\u4E2D\u5199\u9053\u5728\u4E00\u4E2A\u4E09\u7EF4\u7A7A\u95F4\u5916\u52A0\u4E0A\u65F6\u95F4\u5C31\u6210\u4E86\u56DB\u7EF4\u7A7A\u95F4, \u90A3\u5982\u679C\u4EBA\u5DE5\u667A\u80FD\u52A0\u4E0A\u65F6\u95F4\u7684\u7EF4\u5EA6, \u4F1A\u53D8\u6210\u4EC0\u4E48? \u8FD9\u4E2A\u7EF4\u5EA6\u600E\u4E48\u6DFB\u52A0, \u6216\u8005\u662F\u600E\u4E48\u8868\u73B0\u4F53\u73B0\u51FA\u6765, \u53EF\u80FD\u4E0D\u53EB\u65F6\u95F4\u662F\u5176\u4ED6\u7684\u4E1C\u897F, \u65F6\u95F4\u7EF4\u5EA6\u53EA\u662F\u4E00\u4E2A\u8868\u5F81</p><ul><li>\u5047\u4F5C\u771F\u65F6\u771F\u4EA6\u5047\uFF0C\u65E0\u4E3A\u6709\u5904\u6709\u8FD8\u65E0</li></ul><p>\u7531\u865A\u5316\u5B9E\u5219\u662F\u8868\u5F81\u4E3A\u5C06\u6211\u4EEC\u7684\u660E\u8868\u8C61\u548C\u6697\u601D\u8003\u90FD\u7EDF\u7EB3\u4E3A\u5177\u4F53\u7684\u7406\u8BBA, \u5982\u4EFB\u52A1\u9A71\u52A8\u7684\u56E0\u679C\u63A8\u7406\u4E0E\u5B66\u4E60, \u603B\u7ED3\u4E3A: Spatial\uFF0CTemporal and Causal Parse Graph.</p><p>\u7531\u5B9E\u5316\u865A\u5219\u662F\u8BB2\u4EBA\u7684\u4F26\u7406\u9053\u5FB7\u865A\u5316\u4E3A\u76F8\u5E94\u7684\u51FD\u6570, \u57F9\u517B\u673A\u5668\u4EBA\u7684\u4F26\u7406\u9053\u5FB7\u5C31\u662F\u57F9\u517B\u673A\u5668\u4EBA\u7684\u4EF7\u503C\u89C2, \u4EF7\u503C\u89C2\u53EF\u4EE5\u8868\u73B0\u4E3A\u7531\u5B9E\u5316\u865A, \u5373\u5C06\u4E00\u4E2A\u53E0\u8863\u670D\u7684\u4EFB\u52A1\u865A\u5316\u4E3A\u4ECE\u5C71\u811A\u722C\u5230\u5C71\u9876\u7684\u6FC0\u52B1\u4EFB\u52A1.</p><p><img src="https://cdn.jsdelivr.net/gh/scattter/blogweb/images/image-20240830235047824.png" alt="image-20240830235047824"></p><ul><li>\u4EBA\u7684\u4EF7\u503C\u4F26\u7406\u5982\u4F55\u8861\u91CF</li></ul><p>\u54F2\u5B66\u548C\u7ECF\u6D4E\u5B66\u91CC\u9762\u6709\u4E00\u4E2A\u57FA\u672C\u5047 \u8BBE\uFF0C\u8BA4\u4E3A\u4E00\u4E2A\u7406\u6027\u7684\u4EBA\uFF08rational agent\uFF09\uFF0C\u4ED6\u7684\u884C\u4E3A\u548C\u51B3\u7B56\u90FD\u7531\u5229\u76CA\u548C\u4EF7\u503C\u9A71\u52A8\uFF0C \u603B\u5728\u8FFD\u6C42\u81EA\u5DF1\u7684\u5229\u76CA\u6700\u5927\u5316\u3002\u5BF9\u4E8E\u7406\u6027\u7684\u4EBA\uFF0C\u4F60\u901A\u8FC7\u89C2 \u5BDF\u4ED6\u7684\u884C\u4E3A\u548C\u9009\u62E9\uFF0C\u5C31\u53EF\u4EE5\u53CD\u5411\u63A8\u7406\u3001\u5B66\u4E60\u3001\u4F30\u7B97\u4ED6\u7684\u4EF7\u503C\u89C2\u3002\u8FD9\u4E2A\u4EF7\u503C\u89C2\u53EF\u4EE5\u7528\u4E00\u4E2A\u5229\u76CA\u51FD\u6570\u53BB\u8FDB\u884C\u8868\u793A, \u628A\u8FD9\u4E2A\u5229\u76CA \u51FD\u6570\u5B9A\u4E49\u5728\u6D41\u6001\u7684\uFF08fluents\uFF09\u7A7A\u95F4\u91CC\u9762, \u6211\u4EEC\u6BCF\u6B21\u884C\u52A8\uFF0C\u6539\u53D8\u67D0\u4E9B\u6D41\u6001\uFF0C\u4ECE\u800C\u5728 U \u5B9A\u4E49\u7684\u7A7A\u95F4\u4E2D\u5411\u4E0A\u8D70\uFF0C\u4E5F\u5C31\u662F\u201C\u5347\u503C\u201D\u3002\u5C31\u50CF\u662F\u8BF4 \u201C\u6C34\u5F80\u4F4E\u5904\u6D41, \u4EBA\u5F80\u9AD8\u5904\u8D70\u201D \u7C7B\u4F3C, \u4EBA\u548C\u6C34\u90FD\u662F\u6309\u7167\u81EA\u5DF1\u7684\u52BF\u80FD\u51FD\u6570\u5728\u8FD0\u52A8.</p><ul><li>\u7269\u7406\u9886\u57DF\u7684\u7EDF\u4E00(\u7ECF\u5178\u529B\u5B66\u7684\u610F\u4E49)</li></ul><p>\u5728\u770B\u8FD9\u7BC7\u6587\u7AE0\u4E4B\u524D\u6211\u786E\u5B9E\u6CA1\u4ED4\u7EC6\u60F3\u8FC7\u4E3A\u4EC0\u4E48\u7ECF\u5178\u529B\u5B66\u4F1A\u90A3\u4E48\u4EE4\u4EBA\u9707\u64BC, \u5076\u5C14\u7684\u60F3\u4E5F\u662F\u89C9\u5F97\u529B\u5B66\u597D\u50CF\u4E5F\u6CA1\u4EC0\u4E48, \u6CA1\u6709\u6DF1\u5165\u601D\u8003, \u800C\u5728\u6587\u7AE0\u5207\u8EAB\u611F\u53D7\u5230\u4E86\u7ECF\u5178\u529B\u5B66\u7684\u91CD\u8981\u6027. \u5373\u7269\u7406\u5B66\u8BA4\u4E3A\u4E16\u754C\u662F\u7EDF\u4E00\u7684, \u4EFB\u4F55\u4E8B\u7269\u90FD\u6709\u5176\u5BF9\u5E94\u7684\u6A21\u578B, \u201C\u4E00\u5207\u7269\u8D28\u4E8B\u4EF6\u90FD\u5E94\u5F53\u5F52\u7ED3\u4E3A\u4E00\u7CFB\u5217\u7684\u6709\u89C4\u5F8B\u7684\u539F\u5B50\u8FD0\u52A8, \u800C\u4E0D\u5141\u8BB8\u628A\u4EFB\u4F55\u751F\u7269\u7684\u610F\u5FD7\u4F5C\u4E3A\u72EC\u7ACB\u7684\u539F\u56E0\u201C . \u7ECF\u5178\u529B\u5B66\u5C31\u662F\u7B2C\u4E00\u4E2A\u901A\u8FC7\u4E07\u6709\u5F15\u529B\u628A\u5929\u754C\u661F\u4F53\u8FD0\u52A8\u4E0E\u4E16\u4FD7\u7684\u770B\u4F3C\u590D\u6742\u7684\u7269\u4F53\u8FD0\u52A8\u505A\u4E86\u4E00\u4E2A\u7EDF\u4E00\u7684\u89E3\u91CA\u5F62\u6210\u4E00\u4E2A\u79D1\u5B66\u7684\u4F53\u7CFB\uFF0C\u4ECE\u6B64\u4E5F\u575A\u5B9A\u4E86\u5927\u5BB6\u7684\u4FE1\u5FF5\uFF1A \u201C<strong>\u7269\u7406\u4E16\u754C\u5B58\u5728\u7740\u5B8C\u6574\u7684\u56E0\u679C\u94FE\u6761</strong>\u201D\u3002\u5373\u7ECF\u5178\u529B\u5B66\u662F\u9A8C\u8BC1\u7269\u7406\u9886\u57DF\u7EDF\u4E00\u7684\u7B2C\u4E00\u4E2A\u6210\u529F\u793A\u8303 !</p><ul><li>\u7269\u7406\u4E0E\u751F\u7269\u7CFB\u7EDF\u7684\u7ED3\u5408</li></ul><p>\u6B63\u5982\u524D\u9762\u6240\u8FF0, \u771F\u6B63\u7684\u4EBA\u5DE5\u667A\u80FD\u5E94\u8BE5\u662F\u80FD\u591F\u83B7\u53D6\u85CF\u5728\u5BA2\u89C2\u4E16\u754C\u80CC\u540E\u7684 \u201C\u6697\u4FE1\u606F\u201D \u7684, \u4E5F\u53EA\u6709\u5982\u6B64\u624D\u662F\u771F\u6B63\u7684\u4EBA\u5DE5\u667A\u80FD.</p><p>\u751F\u7269\u7CFB\u7EDF\u7684\u57FA\u672C\u524D\u63D0\u662F: \u667A\u80FD\u7269\u79CD\u4E0E\u751F\u4FF1\u6765\u7684\u4EFB\u52A1\u4E0E\u4EF7\u503C\u94FE\u6761\u3002</p><p>\u7269\u7406\u7684\u57FA\u672C\u524D\u63D0\u662F: \u7269\u7406\u73AF\u5883\u5BA2\u89C2\u7684\u73B0\u5B9E\u4E0E\u56E0\u679C\u94FE\u6761\u3002</p><p>\u4EBA\u5DE5\u667A\u80FD\u8981\u53D8\u6210\u667A\u80FD\u79D1\u5B66\uFF0C\u5B83\u672C\u8D28\u4E0A\u5FC5\u5C06\u662F\u8FBE\u5C14\u6587\u4E0E\u725B\u987F\u8FD9\u4E24\u4E2A\u7406\u8BBA \u4F53\u7CFB\u7684\u7EDF\u4E00\u3002</p><p>\u8FD9\u91CC\u8FD8\u6709\u53E6\u4E00\u7BC7\u6587\u7AE0 <a href="https://www.cis.pku.edu.cn/info/1509/2798.htm" target="_blank" rel="noreferrer">\u300AAI \u9700\u7531\u201C\u5FC3\u201D\u9A71\u52A8\uFF0C\u5B9E\u73B0\u201C\u5FC3\u201D\u4E0E\u201C\u7406\u201D\u7684\u52A8\u6001\u5E73\u8861\u300B</a></p><p>\u8FD9\u4E2A\u6587\u7AE0\u4E3B\u8981\u662F\u57FA\u4E8E\u300A\u8D64\u58C1\u8D4B\u300B\u8BA8\u8BBA\u85CF\u5728\u5176\u540E\u7684 \u201C\u51FA\u4E16\u201D \u4E0E \u201C\u5165\u4E16\u201D , \u8FDB\u800C\u5F15\u51FA\u5FC3\u5B66\u548C\u7406\u5B66\u4E24\u79CD\u6D41\u6D3E. \u57FA\u4E8E\u4E24\u4E2A\u6D41\u6D3E\u4E0D\u540C\u7684\u6A21\u578B\u5F15\u51FA \u201CU\u7CFB\u7EDF\u201D \u548C \u201CV\u7CFB\u7EDF\u201D , \u4ECE\u8FD9\u4E2A\u89D2\u5EA6\u9610\u8FF0\u4E0D\u4EC5 <strong>\u667A\u80FD\u4F53</strong> \u8981\u8FBE\u5230**\u201C\u5FC3\u201D\u4E0E\u201C\u7406\u201D\u7684\u5E73\u8861** , \u6211\u4EEC\u6BCF\u4E2A\u4E2A\u4F53\u4E5F\u8981\u5728\u4E0D\u540C\u5883\u9047\u4E2D\u4E0D\u65AD\u8C03\u5E73\u81EA\u5DF1\u7684U\u3001V\u7CFB\u7EDF, \u8BA4\u6E05\u81EA\u5DF1, \u4E0D\u62E7\u5DF4, \u6D3B\u51FA\u81EA\u5DF1\u7684\u4E00\u751F.</p>',24),_=[l];function s(i,o,n,c,d,u){return p(),a("div",null,_)}const m=e(r,[["render",s]]);export{g as __pageData,m as default};
